select CAST(DATEDIFF(s, '1970-01-01', Convert(datetime,DATEDIFF(DAY, 1, SYSUTCDATETIME()))) AS BIGINT) * 1000 + DATEPART(ms, Convert(datetime,DATEDIFF(DAY, 1, SYSUTCDATETIME()))),
CAST(DATEDIFF(s, '1970-01-01', Convert(datetime,DATEDIFF(DAY, 0, SYSUTCDATETIME()))) AS BIGINT) * 1000 + DATEPART(ms, Convert(datetime,DATEDIFF(DAY, 0, SYSUTCDATETIME())));